# 1
def how_many(handle_twitter)
  howmany = handle_twitter.count
  return howmany
end

# 2
def shorter_handle(handle_twitter)
  min_handle = handle_twitter.min_by(&:length)
  return min_handle
end

# 3
def five_letters(handle_twitter)
  sup5_lenght = 0
  array_index = 0
  handle_twitter.each do |i|
    if handle_twitter[array_index].length == 6
      sup5_lenght += 1
    end
    array_index += 1
  end 
  return sup5_lenght
  #handle_twitter.count {|x| x.length == 6} beaucoup plus rapide
end 

# 4
def maj_first(handle_twitter)
  firstupper_lenght = handle_twitter.count{|handle| handle[1].match?( /[[:upper:]]/ )}
  return firstupper_lenght
end 

# 5
def alph_order(handle_twitter)
  alphorder_list = handle_twitter.sort
  return alphorder_list
end

# 6
def asc_order(handle_twitter)
  acsorder_list = handle_twitter.sort_by(&:length)
  return acsorder_list
end

# 7
def epenser_pos(handle_twitter)
  epenserpos = handle_twitter.find_index("@epenser")
  return epenserpos
end

# 8
def length_order(handle_twitter)
  lengthorder = handle_twitter.map(&:length).tally
  newlengthorder = Hash.new
  lengthorder.each do |k,v|
    newlengthorder[k-1] = v
  end
  finallengthorder = Hash.new
  finallengthorder = newlengthorder.sort.to_h.transform_keys(&:to_s).transform_keys{|k| k + " " + "caractère(s)"}.transform_values(&:to_s).transform_values{|v| v + " " + "handle(s)"}
  return finallengthorder
end

# User interface
def welcome_menu
  puts ""
  puts "Bonjour. 👋"
  puts ""
  puts "ℹ️  Voici les informations  dont nous disposons sur cette array de handle twitter:"
  puts ""
  puts "1️⃣  - Combien y a t-il de handle dans cette array ?"
  puts "2️⃣  - Quel est le handle le plus court de cette liste ?"
  puts "3️⃣  - Combien y-a-t'il de handle contenant 5 caractères ?"
  puts "4️⃣  - Combien de handle commencent par une majuscule ?"
  puts "5️⃣  - Trier la liste de handle par ordre alphabétique."
  puts "6️⃣  - Trier la liste de handle par taille."
  puts "7️⃣  - Quelle est la position dans l'array de la personne @epenser."
  puts "8️⃣  - Donner une répartition des handle par taille de ces derniers."
  puts "0️⃣  - Pour quitter le menu."
  puts ""
end

# Gives an answer according to the user's choice
def choose_menu(handle_twitter)
  choice = 1
  until choice == 0
    welcome_menu
    puts "Que souhaitez-vous faire ?"
    puts "(Tapez le chiffre correspondant)"
    print "> "
    choice = gets.chomp.to_i
    puts ""
    if choice == 1
      howmany = how_many(handle_twitter)
      puts "🟢  Il y a #{howmany} handle dans cette array."
    elsif choice == 2
      min_handle = shorter_handle(handle_twitter)
      puts "🟢  Le handle le plus court de cette liste est #{min_handle}."
    elsif choice == 3
      sup5_lenght = five_letters(handle_twitter)
      puts "🟢  Il y a #{sup5_lenght} handle contenant 5 caractères."
    elsif choice == 4
      firstupper_lenght = maj_first(handle_twitter)
      puts "🟢  Il y a #{firstupper_lenght} handle qui commence par une majuscule."
    elsif choice == 5
      alphorder_list = alph_order(handle_twitter)
      puts "🟢  La liste de handle par ordre alphabétique est:"
      puts ""
      puts "#{alphorder_list}"
    elsif choice == 6
      acsorder_list = asc_order(handle_twitter)
      puts "🟢  La liste de handle par taille des handle est:"
      puts ""
      puts "#{acsorder_list}"
    elsif choice == 7
      epenserpos = epenser_pos(handle_twitter)
      puts "🟢  @epenser est à la #{epenserpos}éme position."
    elsif choice == 8
      finallengthorder = length_order(handle_twitter)
      puts "🟢  La répartition des handle par taille de ces derniers est:"
      puts ""
      finallengthorder.each do |k,v|
        puts "Il y a #{v} avec #{k}."
      end 
    elsif choice > 8
      puts "🔴  Vous n'avez pas entré un chiffre de la liste. Veuillez réessayer, merci."
    elsif choice < 0
      puts "🔴  Vous n'avez pas entré un chiffre de la liste. Veuillez réessayer, merci."
    end 
  end
  puts "Merci de votre visite. À bientôt ! 👋"
  puts ""
end 

# Perform
def perform(handle_twitter)
  choose_menu(handle_twitter)
end 

perform(handle_twitter)